INSERT INTO `emails` (`id`,`val`,`name`,`description`,`from`,`subject`,`body`,`bodyhtml`,`type`,`fundraising_campaigns_id`,`lastsent`) VALUES ('1','register_participants_resend_regnum','Participant Registration - Resend Registration Number','Resend the password to the participant if they submit a ''forgot regnum'' request','','Registration for [FAIRNAME]','We have received a request for the retrieval of your registration number from this email address.  Please find your existing registration number below\r\n\r\nRegistration Number: [REGNUM]\r\n','','system',NULL,NULL);
INSERT INTO `emails` (`id`,`val`,`name`,`description`,`from`,`subject`,`body`,`bodyhtml`,`type`,`fundraising_campaigns_id`,`lastsent`) VALUES ('2','new_participant','New Participant','Email that new participants receive when they are added to the system','','Registration for [FAIRNAME]','A new registration account has been created for you.  To access your registration account, please enter the following registration number into the registration website:\r\n\r\nEmail Address: [EMAIL]\r\nRegistration Number: [REGNUM]\r\n','','system',NULL,NULL);
INSERT INTO `emails` (`id`,`val`,`name`,`description`,`from`,`subject`,`body`,`bodyhtml`,`type`,`fundraising_campaigns_id`,`lastsent`) VALUES ('5','register_participants_received','Participant Registration - Form Received','Sent to the participant when the admin flags their signature form as received','','Registration for [FAIRNAME] Complete','Dear [FIRSTNAME],\r\nYour registration for the [FAIRNAME] is now complete.\r\nYour project number is [PROJECTNUMBER].  Please write down your project number and bring it with you to the fair in order to expedite the check-in process.\r\n\r\nSincerely,\r\n [FAIRNAME]','','system',NULL,NULL);
INSERT INTO `emails` (`id`,`val`,`name`,`description`,`from`,`subject`,`body`,`bodyhtml`,`type`,`fundraising_campaigns_id`,`lastsent`) VALUES ('6','register_participants_paymentpending','Participant Registration - Payment Pending','Sent to the participant when the admin flags their signature form as received but payment pending','','Registration for [FAIRNAME] Not Complete - Payment Pending','Dear [FIRSTNAME],\r\nYour registration for the [FAIRNAME] is not yet complete.  We received your registration form however it was missing the required registration fee.  Please send the required registration fee in aso soon as possible in order to complete your registration.\r\n\r\nYour project number is [PROJECTNUMBER].  Please write down your project number and bring it with you to the fair in order to expedite the check-in process.\r\n\r\nSincerely,\r\n [FAIRNAME]','','system',NULL,NULL);
