INSERT INTO `emails` (`id`,`val`,`name`,`description`,`from`,`subject`,`body`,`bodyhtml`,`type`,`fundraising_campaigns_id`,`lastsent`) VALUES ('10','volunteer_new_invite','Volunteers - New Volunteer Invitation','This is sent to a new volunteer when they are invited using the invite volunteers administration section, only available when the Volunteer Registration Type is set to Invite','','Volunteer Registration for [FAIRNAME]','You have been invited to be a volunteer for the [FAIRNAME].  An account has been created for you to login with and complete your information.  You can login to the volunteer registration site with:\n\nEmail Address: [EMAIL]\nPassword: [PASSWORD]\n\nYou can change your password once you login.','','system',NULL,NULL);
